// Merge and parse all `output` JSON strings from incoming items
const merged = items.reduce((acc, item) => {
  const v = item.json?.output;
  if (v == null) return acc;

  try {
    const parsed = typeof v === 'string' ? JSON.parse(v) : v;
    if (Array.isArray(parsed)) acc.push(...parsed);
    else if (parsed && typeof parsed === 'object') acc.push(parsed);
  } catch (_) {
    // ignore bad JSON
  }
  return acc;
}, []);

// ‚úÖ Return a single n8n item; json MUST be an object
return [{ json: { data: merged } }];

