const rowData = $input.all().map((item) => item.json)[0];
const appointmentsBooked = rowData["appointments booked"];
const updatedAppointmentsBooked = appointmentsBooked + 1;

return { updatedAppointmentsBooked };

const rows = $input.all();
const rejectedOffersRow = rows.find((row) => row?.json?.["offer rejected"]);
let rejectedOffersCount =
  parseInt(rejectedOffersRow?.json?.["offer rejected"]) || 0;
rejectedOffersCount += 1;
return { "offer rejected": rejectedOffersCount.toString() };

// Get the input data from the previous node
const inputData = $input.all();

// Initialize aggregation object
const aggregated = {
  emails: [],
  phones: [],
  phonesUncertain: [],
  linkedinurl: [],
  twitters: [],
  instagrams: [],
  facebooks: [],
  youtubes: [],
  tiktoks: [],
  pinterests: [],
  discords: [],
  referrerUrl: null,
  url: null,
  domain: null,
  originalStartUrl: null
};

// Process each item from the input
inputData.forEach(item => {
  const {
    emails = [],
    phones = [],
    phonesUncertain = [],
    linkedinurl = [],
    twitters = [],
    instagrams = [],
    facebooks = [],
    youtubes = [],
    tiktoks = [],
    pinterests = [],
    discords = [],
    referrerUrl,
    url,
    domain,
    originalStartUrl
  } = item.json;

  // Aggregate array fields (avoid duplicates)
  const arrayFields = [
    'emails', 'phones', 'phonesUncertain', 'linkedIns', 
    'twitters', 'instagrams', 'facebooks', 'youtubes', 
    'tiktoks', 'pinterests', 'discords'
  ];

  arrayFields.forEach(field => {
    const fieldData = item.json[field];
    if (Array.isArray(fieldData)) {
      fieldData.forEach(value => {
        if (value && !aggregated[field].includes(value)) {
          aggregated[field].push(value);
        }
      });
    }
  });

  // Set single value fields (use first non-null value)
  const singleFields = ['referrerUrl', 'url', 'domain', 'originalStartUrl'];
  singleFields.forEach(field => {
    const fieldValue = item.json[field];
    if (!aggregated[field] && fieldValue) {
      aggregated[field] = fieldValue;
    }
  });
});

// Create output format that n8n expects
const output = [{
  json: {
    ...aggregated,
    totalItemsProcessed: inputData.length,
    aggregationTimestamp: new Date().toISOString()
  }
}];

return output;

