// Mode: Run Once For All Items
const lead = $('Prepare Lead Data').first().json;
const finalCData = $input.first().json.final_company_data;

const fullName         = lead['Full Name'];
const email            = lead.Email;
const company          = lead.Company;
const jobTitle         = lead['Job Title'];
const companyWebsite   = lead['Company Website'];
const painPoints       = lead['Pain Points / Challenges'];
const leadSource       = lead['Lead Source'];
const submittedAt      = lead.submittedAt;
const formMode         = lead.formMode;
const leadId           = lead.leadId;
const timestamp        = lead.timestamp;

return {
  json: {
    final_company_data: finalCData,
    fullName,
    email,
    company,
    jobTitle,
    companyWebsite,
    painPoints,
    leadSource,
    submittedAt,
    formMode,
    leadId,
    timestamp,
  }
};

// Get input items
const items = $input.all();

// Process each item and add the condition check
return items.map(item => {
  // Parse the date string in M/D/YYYY format
  const lastEmailDateString = item.json['Last Email Sent']; // e.g., "6/14/2025"
  const lastEmail = new Date(lastEmailDateString);
  const today = new Date();

  // Set both dates to midnight for accurate day comparison
  lastEmail.setHours(0, 0, 0, 0);
  today.setHours(0, 0, 0, 0);

  const diffDays = Math.floor((today - lastEmail) / (1000 * 60 * 60 * 24));
  const isFourDaysOrMore = diffDays >= 4;

  return {
    json: {
      ...item.json,
      shouldSendEmail: isFourDaysOrMore,
      daysSinceLastEmail: diffDays,
      lastEmailDate: lastEmailDateString,
      todayDate: today.toLocaleDateString('en-US')
    }
  };
});

