// Get markdown from the previous node (change the key if yours is different)
const md = $input.first().json.output || "";

// Helper to extract values using regex
function extract(regex, input) {
  const match = input.match(regex);
  return match ? match[1].trim() : null;
}

// Extract report date and status
const dateMatch = md.match(/## Report Date\s*([\s\S]+?)\((VALID|TOO OLD)\)/i);
const reportDate = dateMatch ? dateMatch[1].trim() : null;
const reportStatus = dateMatch ? dateMatch[2].trim() : null;

// Extract mandatory sections (Chain of Custody, Certificate Analysis, Site History)
const chainMatch = md.match(/Chain of Custody:\s*(Found|Not Found)[^(]*\(([^)]*)\)/i);
const chainStatus = chainMatch ? chainMatch[1].trim() : null;
const chainDetails = chainMatch ? chainMatch[2].trim() : null;

const certMatch = md.match(/Certificate Analysis:\s*(Found|Not Found)[^(]*\(([^)]*)\)/i);
const certStatus = certMatch ? certMatch[1].trim() : null;
const certDetails = certMatch ? certMatch[2].trim() : null;

const siteMatch = md.match(/Site History:\s*(Found|Not Found)[^(]*\(([^)]*)\)/i);
const siteStatus = siteMatch ? siteMatch[1].trim() : null;
const siteDetails = siteMatch ? siteMatch[2].trim() : null;

// Optional: Waste Volumes, Areas, Consultant Name
const wasteVolume = extract(/Waste Volumes:\s*([^\n]+)/i, md);
const wasteAreas = extract(/Waste Areas:\s*([^\n]+)/i, md);
const consultant = extract(/Consultant Name:\s*([^\n]+)/i, md);

// Output JSON for easy use in IF nodes or next steps
return [
  {
    reportDate,
    reportStatus,
    chainStatus,
    chainDetails,
    certStatus,
    certDetails,
    siteStatus,
    siteDetails,
    wasteVolume,
    wasteAreas,
    consultant
  }
];
