const transformData = (data) => {
  const output = {
    doc_id: "",
    intro: {},
    sources: [],
    policyInterventions: [],
    coreMetricImpactScore: {},
    supportSection: {},
    policyMaker: {},
    supportingExperts: [],
    primaryActions: [],
  };

  data.forEach((item) => {
    const {
      "Section Id": sectionId,
      "Section Name": sectionName,
      "Section Value": sectionValue,
      "Inner Section Value": innerSectionValue,
    } = item;

    switch (sectionId) {
      case "Intro":
        if (sectionName === "Title") output.intro.title = sectionValue;
        if (sectionName === "Description") output.intro.description = sectionValue;
        if (sectionName === "doc_id") output.doc_id = sectionValue;
        break;

      case "Source":
        if (sectionName === "source_sub_text")
          output.sources.push({ subText: sectionValue });
        break;

      case "Policy Interventions":

          output.policyInterventions.push({key: sectionName,value:sectionValue});

 break;
      case "Core Metric/Impact Score":
        // Function to format value
        const formatValue = (value) => {
          if (!value) return '0';

          // Remove any existing symbols/formatting
          const cleanValue = value.toString().replace(/[$,%]/g, '');
          const num = parseFloat(cleanValue);

          if (isNaN(num)) return value.toString();

          // If value contains % or is between 0-100, treat as percentage
          if (value.toString().includes('%') || (num <= 100 && num >= 0)) {
            return `${num}%`;
          }

          // If value contains $ or is large number, treat as currency
          if (value.toString().includes(') || num >= 1000000) {
            return `${num.toLocaleString()}`;
          }

          return num.toLocaleString();
        };

        // Function to create clean key name
        const createKey = (name) => {
          return name.toLowerCase()
                    .replace(/[^a-z0-9\s]/g, '')
                    .trim()
                    .replace(/\s+/g, '_');
        };

        // Process the metric
        const key = createKey(sectionName);
        output.coreMetricImpactScore[key] = {
          value: formatValue(sectionValue),
          description: innerSectionValue || '',
          raw_value: sectionValue
        };
        break;

      case "Support Section":
        if (sectionName === "Support Section Description")
          output.supportSection.description = sectionValue;
        if (sectionName === "Support Section Button Text")
          output.supportSection.buttonText = sectionValue;
        if (sectionName === "Support Section Button Link")
          output.supportSection.buttonLink = sectionValue;
        break;

      case "Policy Maker":

        if (sectionName === "Policy Maker_Item_1_Name")
          output.policyMaker.name = sectionValue;
        if (sectionName === "Policy Maker_Item_1_Designation")
          output.policyMaker.designation = sectionValue;
        break;

      case "Supporting Experts":
        if (sectionName.includes("Supporting_Expert_Item")) {
          const expertIndex = parseInt(sectionName.match(/\d+/)[0]) - 1;
          output.supportingExperts[expertIndex] =
            output.supportingExperts[expertIndex] || {};

          if (sectionName.includes("Name"))
            output.supportingExperts[expertIndex].name = sectionValue;
          if (
            sectionName.includes("Designation") ||
            sectionName.includes("Description")
          ) {
            output.supportingExperts[expertIndex].designation = sectionValue;
          }
        }
        break;

      case "Primary Actions":
        if (sectionName.includes("Primary_Action_Item")) {
          output.primaryActions.push({ item: sectionValue });
        }
        break;

      default:
        break;
    }
  });

  return output;
};

// Example usage
const data = items.map((i) => i.json);
const result = transformData(data);
return result;
