// Get input items
const items = $input.all();

// Process each item and add the condition check
return items.map(item => {
  // Parse the date string in M/D/YYYY format
  const lastEmailDateString = item.json['Last Email Sent']; // e.g., "6/14/2025"
  const lastEmail = new Date(lastEmailDateString);
  const today = new Date();

  // Set both dates to midnight for accurate day comparison
  lastEmail.setHours(0, 0, 0, 0);
  today.setHours(0, 0, 0, 0);

  const diffDays = Math.floor((today - lastEmail) / (1000 * 60 * 60 * 24));
  const isFourDaysOrMore = diffDays >= 4;

  return {
    json: {
      ...item.json,
      shouldSendEmail: isFourDaysOrMore,
      daysSinceLastEmail: diffDays,
      lastEmailDate: lastEmailDateString,
      todayDate: today.toLocaleDateString('en-US')
    }
  };
});

