// Enhanced Code Node to extract sub-workflow IDs from main workflow data
// This handles both Execute Workflow nodes and AI Agent Tool Workflow connections
const workflowData = $('Get a workflow').first().json;
const subWorkflowIds = [];
const subWorkflowInfo = [];
const connectionTypes = {
  executeWorkflow: 0,
  aiAgentTools: 0,
  aiAgentDirect: 0
};

// Check if nodes exist in the workflow data
if (workflowData && workflowData.nodes) {
  // Loop through all nodes to find different types of workflow connections
  workflowData.nodes.forEach(node => {

    // 1. Check for Execute Workflow nodes (original logic)
    if (node.type === 'n8n-nodes-base.executeWorkflow') {
      let workflowId = null;
      let workflowName = null;

      if (node.parameters && node.parameters.workflowId) {
        if (typeof node.parameters.workflowId === 'string') {
          workflowId = node.parameters.workflowId;
        } else if (node.parameters.workflowId.value) {
          workflowId = node.parameters.workflowId.value;
          workflowName = node.parameters.workflowId.cachedResultName || null;
        }
      }

      if (workflowId) {
        subWorkflowIds.push(workflowId);
        subWorkflowInfo.push({
          id: workflowId,
          name: workflowName || `Execute Workflow ${subWorkflowIds.length}`,
          nodeId: node.id,
          nodeName: node.name,
          connectionType: 'executeWorkflow',
          nodeType: node.type
        });
        connectionTypes.executeWorkflow++;
      }
    }

    // 2. Check for AI Agent Tool Workflow nodes (NEW)
    else if (node.type === '@n8n/n8n-nodes-langchain.toolWorkflow') {
      let workflowId = null;
      let workflowName = null;

      if (node.parameters && node.parameters.workflowId) {
        if (typeof node.parameters.workflowId === 'string') {
          workflowId = node.parameters.workflowId;
        } else if (node.parameters.workflowId.value) {
          workflowId = node.parameters.workflowId.value;
          workflowName = node.parameters.workflowId.cachedResultName || null;
        }
      }

      if (workflowId) {
        subWorkflowIds.push(workflowId);
        subWorkflowInfo.push({
          id: workflowId,
          name: workflowName || `AI Tool Workflow ${subWorkflowIds.length}`,
          nodeId: node.id,
          nodeName: node.name,
          connectionType: 'aiAgentTools',
          nodeType: node.type,
          description: node.parameters.description || null
        });
        connectionTypes.aiAgentTools++;
      }
    }

    // 3. Check for other AI Agent nodes that might have direct workflow connections (FUTURE-PROOF)
    else if (node.type && node.type.includes('langchain') && node.type.includes('agent')) {
      // Check if this AI agent node has any workflow references in its parameters
      if (node.parameters) {
        // Look for any parameter that might contain workflow references
        Object.keys(node.parameters).forEach(paramKey => {
          const param = node.parameters[paramKey];

          // Check if this parameter looks like a workflow reference
          if (param && typeof param === 'object' && param.value && 
              (paramKey.toLowerCase().includes('workflow') || 
               (typeof param.value === 'string' && param.value.length > 10))) {

            const workflowId = param.value;
            const workflowName = param.cachedResultName || null;

            // Avoid duplicates
            if (!subWorkflowIds.includes(workflowId)) {
              subWorkflowIds.push(workflowId);
              subWorkflowInfo.push({
                id: workflowId,
                name: workflowName || `AI Agent Direct ${subWorkflowIds.length}`,
                nodeId: node.id,
                nodeName: node.name,
                connectionType: 'aiAgentDirect',
                nodeType: node.type,
                parameterKey: paramKey
              });
              connectionTypes.aiAgentDirect++;
            }
          }
        });
      }
    }
  });
}

// Remove duplicates (in case a workflow is referenced multiple times)
const uniqueSubWorkflowIds = [...new Set(subWorkflowIds)];
const uniqueSubWorkflowInfo = subWorkflowInfo.filter((item, index, self) => 
  index === self.findIndex(t => t.id === item.id)
);

// Return the enhanced results
return {
  mainWorkflowId: workflowData.id,
  mainWorkflowName: workflowData.name,
  subWorkflowIds: uniqueSubWorkflowIds,
  subWorkflowInfo: uniqueSubWorkflowInfo,
  totalSubWorkflows: uniqueSubWorkflowIds.length,
  connectionTypes: connectionTypes,
  originalWorkflowData: workflowData
};
