// Get input data from previous node
const executionData = $input.first().json.data;
const Workflowid = $('When Executed by Another Workflow').first().json.id

// Count successful executions (where finished is true)
const validExecutions = executionData.filter(item => typeof item.finished === 'boolean');
const totalExecutions = validExecutions.length;
const successfullyExecutedCount = validExecutions.filter(item => item.finished === true).length;
const unsuccessfullyExecutedCount = totalExecutions - successfullyExecutedCount;

// Calculate percentages
const successPercentage = totalExecutions > 0 ? 
  Number(((successfullyExecutedCount / totalExecutions) * 100).toFixed(2)) : 0;
const failurePercentage = totalExecutions > 0 ? 
  Number(((unsuccessfullyExecutedCount / totalExecutions) * 100).toFixed(2)) : 0;

// Create workflow link
const workflowLink = `https://gluagents.xyz/workflow/${Workflowid}`;

// Create the statistics object with your specified property names
const stats = {
  "Total executions": totalExecutions,
  "Succesful finished number": successfullyExecutedCount,
  "Unsuccesful finished number": unsuccessfullyExecutedCount,
  "Success percentage": successPercentage,
  "Failure percentage": failurePercentage,
  "Workflow id": Workflowid,
  "Workflow link": workflowLink
};

// Return the statistics as n8n output
return [ { json: stats } ];
